rect.style:
fill:#ffffff;
		       fill-opacity:1;
		       fill-rule:nonzero;
		       stroke:#1a1a1a;
		       stroke-width:5;
		       stroke-linecap:butt;
		       stroke-linejoin:round;
		       stroke-miterlimit:4;
		       stroke-opacity:1;
		       stroke-dasharray:none;
		       stroke-dashoffset:0

text.style:
font-size:30px;
		       font-style:normal;
		       font-variant:normal;
		       font-weight:500;
		       font-stretch:normal;
		       text-align:center;
		       line-height:125%;
		       letter-spacing:0px;
		       word-spacing:0px;
		       text-anchor:middle;
		       fill:#1a1a1a;
		       fill-opacity:1;
		       stroke:none;
		       font-family:OpenComicFont;
		       -inkscape-font-specification:OpenComicFont Medium
